import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useDispatch } from 'react-redux';
import './changePassword.css';
import { changePassword } from '../../actions/auth';

const RegisterReceptionist = () => {

	const initialState = { oldPassword: '', newPassword: '', cnfrmNewPassword: '' };
	const [formData, setFormData] = useState(initialState);
	const dispatch = useDispatch();
	const navigate = useNavigate();
	const [formErrors, setFormErrors] = useState({});
	const [isSubmit, setIsSubmit] = useState(false);

	const handleChange = (e) => {
		setFormData({ ...formData, [e.target.name]: e.target.value })
	}

	const handlePassword = (e) => {
		e.preventDefault();
		console.log(formData)
		setFormErrors(validate(formValues));
		setIsSubmit(true);
		// dispatch(changePassword(formData,navigate));
	}
	return (

		<>

			<div className="limiter">
				<div className="container-login100">
					<div className="wrap-login100">
						<div className="login100-form-title" >
							<span className="login100-form-title-1">
								Change Password
							</span>
						</div>

						<form className="login100-form validate-form">
							<div className="wrap-input100 validate-input m-b-26" data-validate="Username is required">
								<span className="label-input100">Old Password</span>
								<input className="input100" type="text" name="oldPassword" placeholder="Enter Old Password" required={true} onChange={handleChange} />
								<span className="focus-input100"></span>
							</div>

							<div className="wrap-input100 validate-input m-b-18" data-validate="Password is required">
								<span className="label-input100">New Password</span>
								<input className="input100" type="password" name="newPassword" placeholder="Enter New password" required={true} onChange={handleChange} />
								<span className="focus-input100"></span>
							</div>

							<div className="wrap-input100 validate-input m-b-18" data-validate="Password is required">
								<span className="label-input100">New Password</span>
								<input className="input100" type="password" name="cnfrmNewPassword" placeholder="Confirm New password" required="true" onChange={handleChange} />
								<span className="focus-input100"></span>
							</div>
							<div className="container-login100-form-btn">
								<button className="login100-form-btn" onClick={handlePassword}>
									ChangePassword
								</button>
							</div>
						</form>
					</div>
				</div>
			</div>
		</>
	)

}
export default RegisterReceptionist
