import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import './UserProfile.css'
// react-bootstrap components
import {
  Badge,
  Button,
  Card,
  Form,
  Navbar,
  Nav,
  Container,
  Row,
  Col
} from "react-bootstrap";

function User() {
  const navigate = useNavigate();
  const initialState = { Company: 'ADTSC', Username: '', Email: 'aamirsatti507@gmail.com', Firstname: '', Lastname: '', Address: '', City: '', Country: '', Phone: '', Aboutme: '' };
  const [formData, setFormData] = useState(initialState);
  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);


  const handleUserProfile = (e) => {
    e.preventDefault();
    console.log(formData)
    setFormErrors(validate(formData));
    setIsSubmit(true);
  }

  useEffect(() => {
    console.log(formErrors);
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      console.log(Object.keys(formErrors));
      const data=axios.post('http://localhost:5000/updateUser',formData)

      // dispatch(changePassword(formData,navigate)); 
    }
  }, [formErrors]);

useEffect( async() => {
     const {data}= axios.get('http://localhost:5000/getAdmin')
     console.log(data)     
  }, []);



  const validate = (values) => {
    const errors = {};
    //Rules
    // At least 5 alphabets
    //At least 1 letter 
    const regexUsername =  /^[a-zA-Z]{5,}[0-9]+[0-9]*$/;
        //Rules
    //At least 10 letter 
    const regexPhone = /^[\+]?[(]?[0-9]{3}[)]?[-\s\.]?[0-9]{3}[-\s\.]?[0-9]{4,6}$/;
    // const regexUsername = /^[\+]?[(]?[0-9]{3}[)]?[-\s\.]?[0-9]{3}[-\s\.]?[0-9]{4,6}$/;

    if (!values.Username) {
      errors.Username = "Username is Required";
    }
    else if (!regexUsername.test(values.Username)) {
      errors.Username = "This is not a valid Username";
    }
    if (!values.Phone) {
      errors.Phone = "Phone is Required";
    }
    else if (!regexPhone.test(values.Phone)) {
      errors.Phone = "This is not a valid Phone Number";
    }
    if (!values.Firstname) {
      errors.Firstname = "Firstname is Required";
    }
    if (!values.Lastname) {
      errors.Lastname = "Lastname is Required";
    }
    if (!values.Address) {
      errors.Address = "Address is Required";
    }
    if (!values.City) {
      errors.City = "City is Required";
    }
    if (!values.Country) {
      errors.Country = "Country is Required";
    }
    if (!values.Aboutme) {
      errors.Aboutme = "About Me is Required";
    }

    return errors;
  };



  return (
    <>
      <Container fluid>
        <Row>
          <Col md="8">
            <Card>
              <Card.Header>
                <Card.Title as="h4">Edit Profile</Card.Title>
              </Card.Header>
              <Card.Body>
                <Form>
                  <Row>
                    <Col className="pr-1" md="5">
                      <Form.Group>
                        <label>Company (disabled)</label>
                        <Form.Control
                          defaultValue="ADTSC"
                          disabled
                          placeholder="Company"
                          type="text"
                          name="Company"
                        ></Form.Control>
                      </Form.Group>
                    </Col>
                    <Col className="px-1" md="3">
                      <Form.Group>
                        <label>Username</label>
                        <Form.Control
                          // defaultValue="Aamir123"
                          placeholder="Username"
                          type="text"
